import React, { useState, useEffect} from 'react';

import './styles.scss'

const Square = ({id}) => {
    const [ number, setNumber ] = useState(0);

    const handleOnclick = (method) => {
        if (method === 'plus') {
            setNumber(number + 1)
        } else {
            setNumber(number - 1)
        }
    }
    return (
        <div id={id} className="card d-flex">
            <></>
            <p>{number}</p>
            <button
                onClick={() => setNumber(number - 1)} 
            >
                -
            </button>

        </div>
    )
}

export default Square;
